"""	BMR计算器"""def calc_bmr(arr):	try :		gender = arr [ 0 ]		weight = float( arr [ 1 ] )		height = float( arr [ 2 ] )		age = int( arr [ 3 ] )		# gender = input("请输入性别:")		#		# weight = float(input("请输入体重（kg）:"))		#		# age = int(input("请输入年龄："))		#		# height = float(input("请输入身高（cm）："))		if gender == '男' :			# 男性			bmr_val = (13.7 * weight) + (5.0 * height) - (6.8 * age) + 66		elif gender == '女' :			# 女性			bmr_val = (9.6 * weight) + (1.8 * height) - (4.7 * age) + 655		else :			bmr_val = -1		print( )		print( "************** result ***************" )		if bmr_val != -1 :			# print('基础代谢率：', bmr_val, "大卡")			# print( "您的性别：{}，体重：{}公斤，身高：{}厘米，年龄：{}岁".format( gender , weight , height , age ) )			print( "您的性别：{0}，体重：{1}公斤，身高：{2}厘米，年龄：{3}岁".format( gender , weight , height , age ) )			print( "基础代谢率: {} 大卡".format( bmr_val ) )		else :			print( '暂不支持此性别' )	except ValueError :		print( "请输入正确的信息！" )	except IndexError :		print( "输入的信息过少" )def main():	# y_or_n = input("退出程序？（y/n）")	y_or_n = "n"	print("欢迎使用bmr计算器，请按提示操作，谢谢！")	input("按任意键继续：")	while y_or_n != "y":		print("请输入以下信息，以空格分割")		input_str = input("性别 体重（kg） 身高（cm） 年龄：")		str_list = input_str.split(" ")		calc_bmr(str_list)		y_or_n = input("退出程序？（y/n）")		if y_or_n == 'y':			print("感谢使用！")if __name__ == '__main__':	main()