"""	功能：AQI计算	日期：22/01/2019	版本： 1.0"""def cal_aqi(param_list):	"""		AQI计算	"""	pm_val = param_list[0]	co_val = param_list[1]	pm_iaqi = cal_pm_iaqi(pm_val)	co_iaqi = cal_co_iaqi(co_val)	iaqi_list = [ pm_iaqi , co_iaqi ]	aqi = max(iaqi_list)	return aqidef cal_linear(iaqi_lo, iaqi_hi, bp_lo, bp_hi, cp):	"""		线性转换	"""	iaqi = (iaqi_hi - iaqi_lo) * (cp - bp_lo) / (bp_hi - bp_lo) + iaqi_lo	return iaqidef cal_pm_iaqi(pm_val):	if 0 <= pm_val <= 35:		iaqi = cal_linear(0, 50, 0, 35, pm_val)	elif 36 <= pm_val <= 75:		iaqi = cal_linear(50, 100, 35, 75, pm_val)	elif 76 <= pm_val <= 115:		iaqi = cal_linear(100, 150, 75, 115, pm_val)	elif 116 <= pm_val <= 150:		iaqi = cal_linear(150, 200, 115, 150, pm_val)	elif 151 <= pm_val <= 250:		iaqi = cal_linear(200, 300, 150, 250, pm_val)	elif 251 <= pm_val <= 350:		iaqi = cal_linear(300, 400, 250, 350, pm_val)	elif 351 <= pm_val <= 500:		iaqi = cal_linear(400, 500, 350, 500, pm_val)	return iaqidef cal_co_iaqi(co_val):	if 0 <= co_val <= 2:		iaqi = cal_linear(0, 50, 0, 2, co_val)	elif 3 <= co_val <= 76:		iaqi = cal_linear(50, 100, 35, 75, co_val)	elif 76 <= co_val <= 116:		iaqi = cal_linear(100, 150, 75, 115, co_val)	elif 116 <= co_val <= 151:		iaqi = cal_linear(150, 200, 115, 150, co_val)	elif 151 <= co_val <= 251:		iaqi = cal_linear(200, 300, 150, 250, co_val)	elif 251 <= co_val <= 351:		iaqi = cal_linear(300, 400, 250, 350, co_val)	elif 351 <= co_val <= 501:		iaqi = cal_linear(400, 500, 350, 500, co_val)	return iaqidef main():	print('请输入以下信心。用空格分割')	input_str = input('（1）PM2.5(2)CO:')	str_list = input_str.split(' ')	pm_val = float(str_list[0])	co_val = float(str_list[1])	param_list = []	param_list.append(pm_val, co_val)	# 调用AQI计算函数	aqi_val = cal_aqi(param_list)	print('空气质量指数为：{}'.format(aqi_val))if __name__ == '__main__':	main()