"""	功能：AQI计算	日期：23/01/2019	版本： 2.0	读取json文件，排序aqi，将前5的aqi数据输出到json"""import jsondef process_json_file(filepath):	"""		解码json文件	"""	f = open(filepath, mode='r', encoding='utf-8')	# 读取json数据转换为python格式	city_list = json.load(f)	return city_listdef main():	filepath = input('请输入json文件名称:')	city_list = process_json_file(filepath)	# 排序，按city['aqi']	city_list.sort(key=lambda city: city['aqi'])	# 输出aqi前五	top5_list = city_list[:5]	f = open('top5_aqi.json', mode='w', encoding='utf-8')	# 中文输出 ensure_ascii=False	# 输出python格式到json	json.dump(top5_list, f, ensure_ascii=False)	# json.dump(top5_list, f)	f.close()	print(city_list)if __name__ == '__main__':	main()