"""	功能：AQI计算	日期：23/01/2019	版本： 3.0	读取json文件，排序aqi，将aqi数据输出到csv"""import jsonimport csvdef process_json_file(filepath):	"""		解码json文件	"""	f = open(filepath, mode='r', encoding='utf-8')	city_list = json.load(f)	return city_listdef main():	filepath = input('请输入json文件名称:')	city_list = process_json_file(filepath)	city_list.sort(key=lambda city: city['aqi'])	lines = [ list( city_list [ 0 ].keys( ) ) ]	print(lines)	for city in city_list:		lines.append(list(city.values()))		print(list(city.values()))	f = open('beijing_aqi.csv', 'w', encoding='utf-8', newline='')	writer = csv.writer(f)	for line in lines:		# csv写文件		writer.writerow(line)	# csv读文件	# ff = csv.reader(f)	# print("读出来的：", ff)	f.close()	print(city_list)if __name__ == '__main__':	main()