"""	功能：AQI计算	日期：24/01/2019	版本： 4.0	os模块		提供与系统、目录操作相关的功能，不受平台限制		os.remove()             删除文件		os.makedirs()           删除多层目录		os.rmdir()              删除单级目录		os.rename()             重命名文件		os.path.isfile()        判断是否为文件		os.path.isdir()         判断是否为目录		os.path.join()          连接目录，如path1 连接 path2 为 path1/path2		os.path.splitext()      将文件分割成文件名和扩展名，如tmp.txt 分割为 tmp 和 .txt （可用于判断文件类型）	ps：with语句打开文件不需要关闭文件，会自动关闭"""import jsonimport csvimport osdef process_json_file(filepath):	"""		解码json文件	"""	with open(filepath, mode='r', encoding='utf-8') as file:		city_list = json.load(file)	return city_listdef process_csv_file(filepath):	"""		解码csv文件	"""	with open(filepath, mode='r', encoding='utf-8', newline='') as file:		reader = csv.reader(file)		city_list = []		for row in reader:			city_list.append(','.join(row))		# city_list = json.load(file)	return city_listdef main():	filepath = input('请输入json文件名称:')	filename, file_ext = os.path.splitext(filepath)	if file_ext == '.json':		city_list = process_json_file(filepath)	elif file_ext == '.csv':		city_list = process_csv_file(filepath)	else:		city_list = "无法读取该文件"	print(city_list)if __name__ == '__main__':	main()