"""	绘制分形树"""import turtlemax_length = 100index = 15min_length = 5green_size = max_length - min_lengthgreen_size = green_size % indexgreen_size = green_size + index + min_lengthleft_degree = 40right_degree = 20def draw_branch(branch_length):	# 绘制树枝	# 树枝长度小于5则停止绘制	if branch_length > min_length:		if branch_length <= green_size:			turtle.pencolor("green")			turtle.pensize(1)			print("前进变绿色:", branch_length)		# 绘制右侧树枝		turtle.forward(branch_length)		print("向前", branch_length)		turtle.right(right_degree)		print("右转", right_degree)		draw_branch(branch_length - index)		# 绘制左侧树枝		turtle.left(left_degree)		print("左转", left_degree)		draw_branch(branch_length - index)		# 返回之前的树枝		turtle.right(right_degree)		print("右转", right_degree)		if branch_length > green_size:			turtle.pencolor('brown')			turtle.pensize(2)			print("后退变棕色:", branch_length)		turtle.backward(branch_length)		print("向后", branch_length)def main():	turtle.left(90)	turtle.penup()	turtle.backward(max_length + 50)	turtle.pendown()	draw_branch(max_length)	turtle.exitonclick()if __name__ == '__main__':	main()