"""	52周存款计划"""import mathweek_arr = []money_arr = []total_money_arr = []def console_week_schedule():	y_or_n = "n"	while y_or_n != 'y':		week_ipt = int(input("输入你想查询的周数："))		print("第 {} 周情况如下：".format(week_ipt))		print("本周需存款 {} 元，目前总存款额为 {} 元".format(money_arr[week_ipt-1], total_money_arr[week_ipt-1]))		y_or_n = input("退出程序？ y/n")	print("感谢使用，再见！")def main():	money_per_week = float(input("请输入每周的存款金额："))	money_add_week = float(input("请输入每周的递增金额："))	# week = 1	total_weeks = int(input("请输入总共的周数："))	# while week <= total_weeks:	for week in range(total_weeks):		week_arr.append(week+1)		money_arr.append(money_per_week)		# total += money_per_week		total_money_arr.append(math.fsum(money_arr))		print("第 {} 周需存款 {} 元，目前总存款额为 {} 元".format(week+1, money_per_week, math.fsum(money_arr)))		money_per_week += money_add_week		# week += 1	print("*********52周存钱计划数据已计算完成***********")	console_week_schedule()if __name__ == '__main__':	main()