import turtledef draw_recursive_pentagram(size):	"""		迭代绘制五角星	"""	count = 1	while count <= 5:		turtle.forward(size)		turtle.right(144)		count += 1	size += 60	if size <= 340:		draw_recursive_pentagram(size)def main():	# size = 100	# step = 40	# while size <= 100 + step * 4:	# 	count = 1	# 	while count <= 5:	# 		turtle.forward(size)	# 		turtle.right(144)	# 		count = count + 1	# 	size = size + step	#	# turtle.exitonclick()	turtle.penup()	turtle.backward(200)	turtle.pendown()	turtle.pensize(2)	turtle.pencolor('red')	size = 50	draw_recursive_pentagram(size)	turtle.exitonclick()if __name__ == '__main__':	main()